package com.mengka.springboot.ocr_01;

import com.google.gson.Gson;
import com.mengka.springboot.ocr.dto.OcrPrismWordInfo;
import com.mengka.springboot.ocr.dto.OcrReqDto;
import com.mengka.springboot.ocr.dto.OcrRspDto;
import com.mengka.springboot.util.HttpUtils;
import com.mengka.springboot.util.OcrUtil;
import org.apache.http.HttpResponse;
import org.apache.http.util.EntityUtils;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

/**
 *  OCR文字识别
 * https://market.aliyun.com/products/57124001/cmapi028554.html?spm=5176.13057714.zeh3c3lmp.3.c1a77c28BuFZ8R#sku=yuncode2255400000
 *
 *  文档
 *  https://help.aliyun.com/document_detail/30402.html?spm=a2c4g.11186631.6.542.53f91c0c4iX11F
 *
 *  [结果]
 *  sid = d42117cd5d050dc37ce6febef9eab9a069ba5374a2600cf3d57bf3d70f15498fbca37892
 *  word = 性能强大、安全、稳定的云产品
 *  pos = [{"x":61,"y":53},{"x":512,"y":53},{"x":512,"y":86},{"x":61,"y":86}]
 */
public class ocr_01 {

    public static void main(String[] args) {
        String host = "https://ocrapi-advanced.taobao.com";
        String path = "/ocrservice/advanced";
        String method = "POST";
        String appcode = "08ad9fb5eaec4012884aa8987c8880f0";
        Map<String, String> headers = new HashMap<String, String>();
        //最后在header中的格式(中间是英文空格)为Authorization:APPCODE 83359fd73fe94948385f570e3c139105
        headers.put("Authorization", "APPCODE " + appcode);
        //根据API的要求，定义相对应的Content-Type
        headers.put("Content-Type", "application/json; charset=UTF-8");
        Map<String, String> querys = new HashMap<String, String>();

        /* 两个不能同时存在 */
        String imgBase64 = "data:image/png;base64,";
        String url = "";//"https://picture-1254224667.cos.ap-nanjing.myqcloud.com/111.png";

        OcrReqDto ocrReqDto = new OcrReqDto();
        ocrReqDto.setImg(imgBase64);
        String bodys = new Gson().toJson(ocrReqDto);

//        String bodys = "{\"img\":\""+imgBase64+"\",\"url\":\""+url+"\",\"prob\":false,\"charInfo\":false,\"rotate\":false,\"table\":false}";

        try {
            /**
             * 重要提示如下:
             * HttpUtils请从
             * https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/src/main/java/com/aliyun/api/gateway/demo/util/HttpUtils.java
             * 下载
             *
             * 相应的依赖请参照
             * https://github.com/aliyun/api-gateway-demo-sign-java/blob/master/pom.xml
             */
            HttpResponse response = HttpUtils.doPost(host, path, method, headers, querys, bodys);
            System.out.println(response.toString());
            //获取response的body
            String content = EntityUtils.toString(response.getEntity());

            OcrRspDto ocrRspDto = OcrUtil.convertOcrRspDto(content);
            System.out.println("sid = "+ocrRspDto.sid);

            List<OcrPrismWordInfo> ocrPrismWordInfos = ocrRspDto.prismWordsInfo;
            for(OcrPrismWordInfo ocrPrismWordInfo : ocrPrismWordInfos){
                System.out.println("word = "+ocrPrismWordInfo.word);
                System.out.println("pos = "+ new Gson().toJson(ocrPrismWordInfo.pos));
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
    }
}
